def getBalance():
    balance = random.randint(1000, 99999999)
    return balance

def AccountNumber(typeOfAccount):
    initial = random.randint(10000000,99999999)

    temp = initial
    s = 0
    count = 0
    while(temp>=1):
        if count%2 == 0:
            factor = 1
        else:
            factor = 2

        s = s + factor*(temp%10)
        temp = int(temp/10)
        count = count+1
    s = 9*s
    s = s%10
    if typeOfAccount == 'L':
        pre1 = 1000
    elif typeOfAccount == 'S':
        pre1 = 1100
    else:
        pre1 = 1010

    ac_number = str(pre1)+ str(initial)+ str(s)
    return ac_number

